import React, { useState } from 'react';
import AceEditor from 'react-ace';

import 'ace-builds/src-noconflict/mode-java';
import 'ace-builds/src-noconflict/theme-github';

function App() {
  const [javaCode, setJavaCode] = useState('');

  const sendJavaCode = async () => {
    try {
      let response = await fetch('http://localhost:3000/routes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code: javaCode }),
      });

      let data = await response.json();
      console.log(data);
    } catch (error) {
      console.error("Error sending code:", error);
    }
  };

  return (
    <div className="App">
      <AceEditor
        mode="java"
        theme="github"
        value={javaCode}
        onChange={(value) => setJavaCode(value)}
        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
      {/* Later, you'll add a button to send this data */}
      <button onClick={sendJavaCode}>Submit</button>
    </div>
  );
}

export default App;
