import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const ServicesPanel = () => {
  // State to keep track of which services are toggled
  const [serviceStates, setServiceStates] = useState({
    kafka: false,
    postgre: false,
  });

  // Handler for toggling a service
  const handleToggle = (service) => {
    setServiceStates({
      ...serviceStates,
      [service]: !serviceStates[service],
    });
  };

  // Handler for deploying services
  const handleDeploy = async () => {
    // Prepare your request data based on serviceStates
    const requestData = { ...serviceStates };

    // Send a request to your backend (or Node.js server)
    const response = await fetch('/api/deploy', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(requestData),
    });

    // Handle response...
  };

  return (
    <Container>
      <Row>
        {Object.keys(serviceStates).map(service => (
          <Row key={service}>
            <Form.Check 
              type="switch"
              id={service}
              label={service}
              checked={serviceStates[service]}
              onChange={() => handleToggle(service)}
            />
          </Row>
        ))}
      </Row>
      <Row>
        <Col>
          <Button onClick={handleDeploy}>Deploy</Button>
        </Col>
      </Row>
    </Container>
  );
};

export default ServicesPanel;
