import React, { useState, useEffect } from 'react';
import AceEditor from 'react-ace';

import 'ace-builds/src-noconflict/mode-java';
import 'ace-builds/src-noconflict/theme-github';

function App() {
  const [javaCode, setJavaCode] = useState('');
  const [logs, setLogs] = useState("");

  const sendJavaCode = async () => {
    try {
      let response = await fetch('http://localhost:8080/routes', {
        method: 'POST',
        headers: {
          'Content-Type': 'text/plain',
        },
        body: javaCode,
      });

      let data = await response.text();
      console.log(data);
    } catch (error) {
      console.error("Error sending code:", error);
    }
  };

  useEffect(() => {
    const options = {
      connectionTimeout: 4000, // Set the connection timeout (ms)
      maxRetries: 10,          // Set the maximum number of connection retries
    };

    const ws = new ReconnectingWebSocket('ws://localhost:8080/ws/logs', [], options);

    ws.addEventListener('message', (event) => {
      setLogs(prevLogs => prevLogs + event.data);
    });

    // Clean up the connection when the component unmounts
    return () => ws.close();
  }, []);

  return (
    <div className="App">
      <AceEditor
        mode="java"
        theme="github"
        value={javaCode}
        onChange={(value) => setJavaCode(value)}
        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
      <button onClick={sendJavaCode}>Submit</button>
      <div>
          <h2>Live Logs:</h2>
          <pre>{logs}</pre>
      </div>
    </div>
  );
}

export default App;
