import * as nls from 'monaco-editor/esm/vs/nls.js';
import { QuickTextSearchActionId } from '../common/constants.js';
import { registerAction2, Action2 } from 'monaco-editor/esm/vs/platform/actions/common/actions.js';
import { category } from './searchActionsBase.js';
import { IQuickInputService } from 'monaco-editor/esm/vs/platform/quickinput/common/quickInput.js';
import { TEXT_SEARCH_QUICK_ACCESS_PREFIX } from './quickTextSearch/textSearchQuickAccess.js';
registerAction2(class TextSearchQuickAccessAction extends Action2 {
    constructor() {
        super({
            id: QuickTextSearchActionId,
            title: {
                value: ( nls.localize('quickTextSearch', "Quick Text Search (Experimental)")),
                original: 'Quick Text Search (Experimental)'
            },
            category,
            f1: true
        });
    }
    async run(accessor, match) {
        const quickInputService = accessor.get(IQuickInputService);
        quickInputService.quickAccess.show(TEXT_SEARCH_QUICK_ACCESS_PREFIX);
    }
});
